
//11.Pattern Question
public class StarPattern {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=5;			//number of rows 
		int i,j;			//rows= i & columns= j
		for(i=0;i<=n;i++){
			for(j=0;j<i;j++){
				System.out.print("* ");
			}
				System.out.println();
			}
		

	}

}
